// Loop over input items and add a new field called 'myNewField' to the JSON of each one
const result = {
  "video_id" : ""
}
for (const item of $input.all()) {
  result["video_id"] = item.json.URL.split("=")[1]
}

return result

let items = []


let temp_transcript = "";

for (const item of $input.all()) {
  for (const element of item.json.transcript.text) {
    temp_transcript += element._;

    while (temp_transcript.length >= 2000) {
      items.push({ transcript: temp_transcript.slice(0, 2000) });
      temp_transcript = temp_transcript.slice(2000); // Keep the remaining part
    }
  }
}

// Push any remaining transcript that didn't reach 2000 characters
if (temp_transcript.length > 0) {
  items.push({ transcript: temp_transcript });
}

return items
